mapper.select:
SELECT loginId
FROM admin
WHERE loginId = #{loginId}

mapper.update:
UPDATE admin
SET adminName = #{adminName},
loginId = #{loginId},
loginPw = #{loginPw},
businessNumber = #{businessNumber}
WHERE adminId = #{adminId}

mapper.select:
SELECT adminId, adminName, loginId, loginPw, businessNumber
FROM admin
WHERE adminId = #{adminId}
