mapper.select:
-- 1) 상품이 중분류에 직접 연결된 경우 SELECT DISTINCT sub.* FROM productCategory sub JOIN product p ON p.categoryId = sub.categoryId JOIN client_product cp ON cp.productId = p.productId WHERE cp.clientId = #{clientId} AND sub.parentId = #{parentId}
UNION
-- 2) 상품이 대분류에 연결되었지만, 그 대분류의 하위 중분류 가져오기 SELECT DISTINCT sub.* FROM productCategory sub WHERE sub.parentId = #{parentId}
